mapper.insert:
INSERT INTO
    post
(
  title
, content
, file_path
, created_date
, creator
, modified_date
, modifier
)

VALUES(
          #{title}
      , #{content}
      , #{filePath}
      , datetime('now', 'localtime')
      , #{creator}
      , #{modifiedDate}
      , #{modifier}
      )

INSERT INTO
    attachment_file
(
    post_number
, uuid_name
, file_name
, created_date
, creator
, modified_date
, modifier
)

VALUES(
          #{postNumber}
      , #{uuidName}
      , #{fileName}
      , datetime('now', 'localtime')
      , #{creator}
      , #{modifiedDate}
      , #{modifier}
      )

INSERT INTO
    hashtag
(
    hashtag_id,
    hashtag_cont
)

VALUES(
        #{id},
        #{content}
      )
